function world_prayer_main()
{
  global $country_isos;

  echo'<h2>World Prayer</h2>';
  echo '<p>This plugin retrieves demographic data from the CIA World Factbook via https://github.com/factbook/factbook.json<br />This plugin provides outbound links for countries to the Operation World website and Amazon for the Operation World book</p>';

  if(!empty($_GET['action']))
  {
      switch($_GET['action'])
      {
        case 'create-nations-shortcode':
            echo world_prayer_nations_shortcode();
        break;
        case 'create-page':
            echo world_prayer_create_page();
        break;

      }
  }
  else
  {
    //default screen

    echo'<p><a href="admin.php?page=pray-for-countries/index.php&action=create-nations-shortcode" class="button-primary">Create shortcode with chosen nations(s)</a></p>';
 
    $dropdown=world_prayer_dropdown();
    if(!empty($dropdown))
    {
      $allowed_html= array('option'=> array( 'selected' => array(), 'value' => array() ) );
      echo'<form action="" method="POST"><p>Choose Country<select name="iso">'.wp_kses($dropdown,$allowed_html).'</select><input type="submit" class="button-primary"/></p></form>';

    }
    
    
    $country = !empty($_REQUEST['iso'])?sanitize_text_field(  $_REQUEST['iso'] ):'AF';
    //check data is allowed
    if(!empty( $country ) && !in_array( $country, $country_isos ) ){
      $country = 'AF';
    }
      //$country is in the list of allowed input, so safe to use.
    
    echo worldprayer_fetch_country( $country );
    
  }
  
}


function world_prayer_dropdown()
{
  global $wpdb,$country_isos;
  $output='';
  $iso=!empty($_REQUEST['iso'])?sanitize_text_field( $_REQUEST['iso'] ):null;
  //only use inputted $iso if in allowed list
  if( empty($iso) || !in_array($iso,$country_isos)){
    $iso=null;
  }
  $countries=$wpdb->get_results('SELECT * FROM '.WPR_TBL.' ORDER BY country');
  if(!empty($countries))
  {
    foreach($countries AS $c)
    {
      $output.='<option value="'.esc_html( $c->iso_country_code ).'" '.selected( $c->iso_country_code, $iso, false ).'>'.esc_html( $c->country ).'</option>';
    }
    return $output;
  }
  else return FALSE;
}

function world_prayer_checkboxes()
{
  global $wpdb,$country_isos;
  $output='<p><input type="checkbox" id="checkAll">Check All
  </p><table class="form-table"><tbody>';
  $iso=!empty($_REQUEST['iso'])?sanitize_text_field( $_REQUEST['iso'] ):null;
  //check iso is in allowed list or give AF value
  if(!in_array($iso,$country_isos))$iso='AF';


  $countries=$wpdb->get_results('SELECT * FROM '.WPR_TBL.' ORDER BY country');
  if(!empty($countries))
  {
    foreach($countries AS $c)
    {
      $output.='<tr><th scope="row">'.esc_html($c->country).'</th><td><fieldset><legend class="screen-reader-text"><span>'.esc_html($c->country).'</span></legend><label for="'.esc_html($c->country).'"><input class="country" name="iso[]" type="checkbox"  id="'.esc_html($c->country).'" value="'.esc_html($c->iso_country_code).'"></label></fieldset></td></tr>';
    
    }
    $output.='</tbody></table>';
    $output.='<script>jQuery(document).ready(function($){ $("#checkAll").click(function () {
      $("input:checkbox").not(this).prop("checked", this.checked);
  });});</script>';
    return $output;
  }
  else return FALSE;
}

function worldprayer_fetch_country($iso)
{
  global $wpdb,$country_isos;
  
  if(empty ($iso)) {
    return "<p>No country selected</p>";
  }

  if(!in_array( $iso, $country_isos ) ) {
    return "<p>Invalid country code</p>";
  }
  //$iso is safe to use as in allowed list of input

  $allowed_html = array(
    'a' => array(
        'href' => array(),
        'title' => array()
    ),
    'br' => array(),
    'em' => array(),
    'strong' => array(),
);





  $country=$wpdb->get_row('SELECT * FROM '.WPR_TBL.' WHERE iso_country_code="'.esc_sql( strtoupper( $iso ) ).'"');
 
  if(!empty($country))
  {
    $TS=time()-700000;
    if(!empty($country->last_updated))$TS=strtotime($country->last_updated);
    if($TS<time()-604800 && is_admin())
    {
      
        //data is more than a week old, so sync again.
        $request = wp_remote_get( esc_url($country->json_loc));
        if( is_wp_error( $request ) ) {
          $error_string = $result->get_error_message();
	        echo '<div id="message" class="error"><p>' . esc_html($error_string) . '</p></div>';
          
        }
        else
        {
          $body = wp_remote_retrieve_body( $request );
          $data = json_decode( $body );
          
          $sanitized_data = array();
          $sql_safe = array();
         
         

          //sanitize data
          $sanitized_data['background'] = !empty($data->Introduction->Background->text )?wp_kses( stripslashes( $data->Introduction->Background->text ), $allowed_html):null;
          $sanitized_data['location'] = !empty($data->Geography->{'Geographic coordinates'}->text ) ? wp_kses( stripslashes( $data->Geography->{'Geographic coordinates'}->text ), $allowed_html):null;
          $sanitized_data['population'] = !empty($data->{'People and Society'}->Population->text)? wp_kses( stripslashes( $data->{'People and Society'}->Population->text ), $allowed_html):null;
          $sanitized_data['people_groups'] = !empty($data->{'People and Society'}->{'Ethnic groups'}->text)? wp_kses( stripslashes( $data->{'People and Society'}->{'Ethnic groups'}->text ), $allowed_html):null;
          $sanitized_data['religions'] = !empty($data->{'People and Society'}->Religions->text )? wp_kses( stripslashes( $data->{'People and Society'}->Religions->text ), $allowed_html):null;
          $sanitized_data['life_expectancy'] = !empty($data->{'People and Society'}->{'Life expectancy at birth'}->{'total population'}->text)? wp_kses( stripslashes( $data->{'People and Society'}->{'Life expectancy at birth'}->{'total population'}->text ), $allowed_html):null;

          //make safe for sql
          foreach($sanitized_data AS $key => $value) {
            $sql_safe[$key] = esc_sql( $value);
          }


          $wpdb->query('UPDATE '.WPR_TBL.' SET background="'.$sql_safe['background'].'",
          location="'.$sql_safe['location'].'",population="'.$sql_safe['population'].'",people_groups="'.$sql_safe['people_groups'].'",religions="'.$sql_safe['religions'].'", life_expectancy="'.$sql_safe['life_expectancy'].'",last_updated="'.date('Y-m-d').'" WHERE iso_country_code="'.esc_sql($iso).'"');
         
          //refetch data
          $country=$wpdb->get_row('SELECT * FROM '.WPR_TBL.' WHERE iso_country_code="'.esc_sql(strtoupper($iso)).'"');
        }

    }
    $image=plugins_url('/flags/'.esc_html(strtolower($country->iso_country_code)).'.svg',__FILE__ );
    $adminOutput='<table class="table"><tr><td width="25%"><img style="border:1px SOLID #CCC;" src="'.esc_url($image) .'" alt="'.esc_html($country->country).'"/><td><td><h3>'.esc_html($country->country).'</h3><p></p><p><strong>Population:</strong> '.esc_html($country->population).'</p><p><strong>Life Expectancy:</strong> '.esc_html($country->life_expectancy).'</p><p><strong>People groups:</strong> '.wp_kses($country->people_groups,$allowed_html).'</p><p><strong>Religions:</strong> '.wp_kses($country->religions,$allowed_html).'</p><p><a target="_blank" href="'.esc_url($country->operation_world_link).'" class="button-primary">Prayer points for '.wp_kses($country->country,$allowed_html).'</a></p></td></tr>';
    return $adminOutput;

  
  }
  else
  {
    return '<p>'.esc_html($iso).' Country not found</p>';
  }


}


function world_prayer_nations_shortcode()
{
    global $wpdb,$country_isos;
    
    if(!empty($_POST['iso']))
    {
      $sanitized=array();//array of posted isos that are expected inputs
        //check in allowed list
        $sanitizedPostedISOs=world_prayer_sanitize_text_or_array_field($_POST['iso']);  
        foreach($sanitizedPostedISOs AS $key=>$posted_iso)
        {
          if(in_array( $posted_iso, $country_isos ) ){
            $sanitized[]=$posted_iso;
          }

        }
        if(empty($sanitized))return FALSE;
        $count=count($sanitized);
        $countries=implode(",",$sanitized);
        
        
        $shortcode='[world-prayer iso="'.esc_html($countries).'"]';
        $output='<h3>World Prayer shortcode</h3>';
        $output.='<p><a href="admin.php?page=pray-for-countries/index.php" class="button-primary">Back to main menu</a></p>';
        $output.='<p><a href="admin.php?page=pray-for-countries/index.php&action=create-nations-shortcode" class="button-primary">Redo Shortcode</a></p>';
        $output.='<p>'.$shortcode.' for '.(int)$count.' countries</p>';
        update_option('world_prayer_chosen_nations',$countries);
        return $output;
    }
    $chosen_nations=array();
    $chosen_nations=get_option('world_prayer_chosen_nations');
    $checkboxes=world_prayer_checkboxes($chosen_nations);
    $output='<h3>Choose nations to include in your shortcode</h3><form action="" method="POST">';
    $output.=$checkboxes;
    $output.='<input type="submit" class="button-primary"/></p></form>';
    return $output;
}



add_shortcode('world-prayer','world_prayer_shortcode');
function world_prayer_shortcode($atts, $content =null)
{
  global $country_isos;
  extract(shortcode_atts(array('iso'=>NULL,'count'=>false),$atts));
  $out='<div class="aligncenter">';
  $isoCodes=explode(',',$iso);
  if ( !empty( $count ) ){
      $nationsCount=count($isoCodes);
      if(!empty($nationsCount))
      {
        $out.='<p>'.esc_html( sprintf( '%1$s nations', $nationsCount ) ).'</p>';
      }
  }
  foreach($isoCodes AS $key=>$ISOcode)
  {
    if(in_array($ISOcode,$country_isos)){
      //only use if an expected country $ISOcode
      $out.=worldprayer_fetch_country($ISOcode);
    }
  }
  $locale=get_locale();

  switch($locale)
